def-group:


      Disable basic-auth-file for the API Server
      {{{- oval_affected(products) }}}
      basic-auth-file should not be configured on the master node.










{{%- if product == "ocp4" %}}

    /etc/kubernetes/static-pod-resources/configmaps/config/config.yaml
    ^.*"basic-auth-file"\:[\s]*\[.*"(\S+)".*\][,]*.*$
    1

{{% else %}}

    /etc/origin/master/master-config.yaml
    ^[\s]*basic-auth-file\:[\n]+[\s*]-[\s]*(\S+)[\s]*$
    1

{{%- endif %}}
