def-group:


      Configure the etcd Certificate for the API Server
      {{{- oval_affected(products) }}}
      The certificate should be configured in etcdClientInfo.











{{%- if product == "ocp4" %}}

    /etc/kubernetes/static-pod-resources/configmaps/config/config.yaml
    ^.*"storageConfig":[\s]*{.*"certFile":[\s]*"([^\s"']+)",.*$
    1

{{% else %}}

    /etc/origin/master/master-config.yaml
    ^[\s]*etcdClientInfo\:(?:[^\n]*\n+)+?[\s]*certFile\:[\s]+(\S+)[\s]*$
    1

{{%- endif %}}


    ^master.etcd-client.crt|/etc/kubernetes/static-pod-resources/secrets/etcd-client/tls.crt$
