def-group:


      Configure the Certificate Key for the API Server
      {{{- oval_affected(products) }}}
      The certificate key should be configured in servingInfo.











{{%- if product == "ocp4" %}}

    /etc/kubernetes/static-pod-resources/configmaps/config/config.yaml
    >^.*"servingInfo":[\s]*{.*"keyFile":[\s]*"([^\s"']+)",.*$
    1

{{% else %}}

    /etc/origin/master/master-config.yaml
    ^[\s]*servingInfo\:(?:[^\n]*\n+)+?[\s]*keyFile\:[\s]+(\S+)[\s]*$
    1

{{%- endif %}}


    ^master.server.key|/etc/kubernetes/static-pod-certs/secrets/service-network-serving-certkey/tls.key$
