def-group:


      Disable Token-based Authentication
      {{{- oval_affected(products) }}}
      token-auth-file should not be configured on the master node.










{{%- if product == "ocp4" %}}

    /etc/kubernetes/static-pod-resources/configmaps/config/config.yaml
    ^.*"apiServerArguments":[\s]*{.*"token-auth-file":[\s]*\[.*"(\S+)".*\][,]*.*$
    1

{{% else %}}

    /etc/origin/master/master-config.yaml
    ^[\s]*token-auth-file\:[\n]+[\s*]-[\s]*(\S+)[\s]*$
    1

{{%- endif %}}
